object DatabaseSeeder {
    
    fun seedDatabase(context: Context) {
        val database = AppDatabase.getDatabase(context)
        val productDao = database.productDao()
        
        CoroutineScope(Dispatchers.IO).launch {
            // Insertar productos directamente - Room manejará duplicados
            // Si ya hay productos, simplemente no se insertarán duplicados
            try {
                productDao.insertProducts(getSeedProducts())
            } catch (e: Exception) {
                // Si hay error, probablemente ya existen productos
                android.util.Log.d("DatabaseSeeder", "Error al insertar productos: ${e.message}")
            }
        }
    }
    
